If(Homepage1="ScreenCheckpoints",Set(Homepage1,""));
If(Homepage2="ScreenCheckpoints",Set(Homepage2,""));
If(Homepage3="ScreenCheckpoints",Set(Homepage3,""));
If(Homepage4="ScreenCheckpoints",Set(Homepage4,""));

If(!IsBlank(Homepage4),
    //Notify("Homepage4...");;
    Switch(Homepage4, 
        "ScreenChecklists", 
            Set(Homepage4,"");
            Navigate(ScreenChecklists),
        //Standardergebnis
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage3),
    //Notify("Homepage3...");;
    Switch(Homepage3, 
        "ScreenChecklists", 
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenChecklists),
        //Standardergebnis
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage2),
    //Notify("Homepage2...");;
    Switch(Homepage2, 
        "ScreenChecklists", 
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenChecklists),
        //Standarsergebnis
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
    //Notify("Homepage1...");;
    Switch(Homepage1, 
        "ScreenChecklists", 
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenChecklists),
        //Standarsergebnis
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
)));

Set(ShowDeleteDialog,false);
Set(_ShowLoadingHint,true);

//Pos = 99 for predecessor
Patch(MyCheckpoints, 
    LookUp(MyCheckpoints,Pos=SCheckpointPos-1),
    {Pos:99}
);

//Pos = Pos -1 for selected entry
Patch(MyCheckpoints, 
    LookUp(MyCheckpoints,Pos=SCheckpointPos),
    {Pos:SCheckpointPos-1}
);

//Pos = current pos for predecessor
Patch(MyCheckpoints, 
    LookUp(MyCheckpoints,Pos=99),
    {Pos:SCheckpointPos}
);

//Update SP list
ForAll(MyCheckpoints As aSource,
    Patch(pssChecklistsCheckpoints,
        LookUp(pssChecklistsCheckpoints,
        Title=aSource.Title&&
        Checkpoint=aSource.Checkpoint),
        {Pos:aSource.Pos}
    )
);
   
//Set(InitialReadCheckpoints;true);;
//Set(ReadCheckpoints;true);;
Set(CheckpointSelected,true);
Set(_ShowLoadingHint,false);

OnlineStatus=1 && !IsBlank(SelectedCheckpoint)
//es wurde keine Daten für den Checkpunkt erfasst
/*
&& IsBlank(LookUp(pssChecklistsActivitiesP;
Titel=SelectedChecklist && Checkpoint=SelectedCheckpoint;ID))
*/

Set(ShowDeleteDialog,false);

Set(SCheckpointPos,0);
Set(SCheckpointText,"");
Set(SCheckpointDescription,"");
Set(SCheckpointRequired,true);

Set(SCheckpointStatus1,"");
Set(SCheckpointStatus2,"");
Set(SCheckpointStatus3,"");
Set(SCheckpointStatus4,"");
Set(SCheckpointStatus5,"");

Clear(colFlexForm);

Set(SCheckpointFlexForm,"");

Set(SCheckpointF1,"");
Set(SCheckpointF2,"");
Set(SCheckpointF3,"");
Set(SCheckpointF4,"");
Set(SCheckpointF5,"");

Set(SCheckpointF1Label,"");
Set(SCheckpointF2Label,"");
Set(SCheckpointF3Label,"");
Set(SCheckpointF4Label,"");
Set(SCheckpointF5Label,"");

Set(NPos,Max(MyCheckpoints,Pos)+1);
ResetForm(EditForm1_19);
NewForm(EditForm1_19);
Navigate(ScreenCheckpointAdd);

Set(ShowDeleteDialog,false);
Set (locErrorCode, "");
//--------------------------------------------------------
// Delete checklist
If(IsBlank(locErrorCode),

    // remove from collection
    RemoveIf(MyCheckpoints,
        Title=SelectedChecklist&&
        Checkpoint=SelectedCheckpoint); 
    
    //all good?
    If(!IsEmpty(Errors([@MyCheckpoints])),
        Set(locErrorCode, 
            LookUp(colLanguage,Title="Error! Was not able to delete the checkpoint").Wert
        );
        Notify(
            locErrorCode,
            //LookUp(MyUVVSettings;
            //Title="Fehler beim Löschen in UVVKundenliste").Wert;
            NotificationType.Error,2000
        );
    ,// Else
        Set (locErrorCode, "");
    );

    // Save checkpoints locally
    If(MyDeviceType="Mobile" && IsBlank(locErrorCode),
        SaveData(MyCheckpoints,"MyCheckpoints")  
    );

    // Generate pos number
    If(IsBlank(locErrorCode),
        ClearCollect(
            MyCheckpoints,
            ForAll(
                Sequence(CountRows(MyCheckpoints)),
                Patch(
                    Last(FirstN(MyCheckpoints, Value)),
                    {Pos:Value}
                )
            )
        )
    );

    //Remove from SP list
    If(IsBlank(locErrorCode) && OnlineStatus=1,
        RemoveIf(pssChecklistsCheckpoints,
            Title=SelectedChecklist&&
            Checkpoint=SelectedCheckpoint
        ); 
        RemoveIf(pssChecklistsCheckpointsText,
            Title=SelectedChecklist&&
            Checkpoint=SelectedCheckpoint
        ); 
        ForAll(MyCheckpoints As aSource,
            Patch(pssChecklistsCheckpoints,
                LookUp(pssChecklistsCheckpoints,
                Title=aSource.Title&&
                Checkpoint=aSource.Checkpoint),
                {Pos:aSource.Pos}
            )
        );
    );

    If(CountRows(MyCheckpoints)>0,
        Set(SelectedCheckpoint,
            First(MyCheckpoints).Checkpoint
        );
        Set(CheckpointSelected,true);
    ,//Else
        Set(SelectedCheckpoint,"");
        Set(CheckpointSelected,false);
    );
    
    //Set(InitialReadCheckpoints;true);;
    //Set(ReadCheckpoints;true);;

);

If(Connection.Connected,
    Set (OnlineStatus, 1)
,// Else    
    Set (OnlineStatus, 0)
);
//---------------------------------------------
//Load all checkpoints from SP or from cache to local collections
If(IsBlank(InitialReadCheckpoints)||InitialReadCheckpoints,
    Set(_ShowLoadingHint,true);
    //-----------------------------------------------------
    Set(InitialReadCheckpoints,false);
    //-----------------------------------------------------
    If(OnlineStatus=1,
        //-------------------------------------------------
        //load checkpoints from SP list
        Set(FooterText,"Loading checkpoints");
        Refresh(pssChecklistsCheckpoints);
        ClearCollect(colCheckpoints,
            ShowColumns(
                Filter(pssChecklistsCheckpoints,
                    Load=true
                ),
                ID,Title,Checkpoint,Pos,Required,PossibleStatus,Status1,Status2,Status3,Status4,Status5,FlexForm
            )
        );
        // Save checkpoints locally
        If(MyDeviceType="Mobile",
            SaveData(colCheckpoints,"Checkpoints")  
        );
        //-------------------------------------------------
        Set(FooterText,"Loading checkpoints text");
        Refresh(pssChecklistsCheckpointsText);
        ClearCollect(colCheckpointsText,
            ShowColumns(
                Filter(pssChecklistsCheckpointsText,
                    Load=true
                ),
                ID,Title,Checkpoint,CheckpointText,Description,LanguageTag
            )
        );
        // Save checkpoints text locally
        If(MyDeviceType="Mobile",
            SaveData(colCheckpointsText,"CheckpointsText")  
        );
    ,//Else
        If(MyDeviceType="Mobile",
            Clear(colCheckpoints);
            Clear(colCheckpointsText);
            
            Set(FooterText,"Loading checkpoints from cache");
            LoadData(colCheckpoints,"Checkpoints",true);

            Set(FooterText,"Loading checkpoints text from cache");
            LoadData(colCheckpointsText,"CheckpointsText",true);
        )
    );
    Set(FooterText,"");
    Set(_ShowLoadingHint,false); 
);
//------------------------------------------------
//Load checkpoints
If(IsBlank(ReadCheckpoints)||ReadCheckpoints,
    Set(_ShowLoadingHint,true);
    //-----------------------------------------------------
    Set(FooterText,"Loading checkpoints");
    //-----------------------------------------------------
    Set(ReadCheckpoints,false);
    //-----------------------------------------------------
    If(IsBlank(CheckpointsSortDescending),
        Set(CheckpointsSortDescending,false)
    );
    //-----------------------------------------------------
    //load checkpoints from local collections
    ClearCollect(MyCheckpoints,
        SortByColumns(
            AddColumns(
                ShowColumns(
                    Filter(
                        colCheckpoints,
                        Title=SelectedChecklist
                    ),
                    Title,Checkpoint,Pos,Required,Status1,Status2,Status3,Status4,Status5,FlexForm
                ) As aSource,

                CheckpointText,
                    LookUp(colCheckpointsText,
                    Title=aSource.Title&&
                    Checkpoint=aSource.Checkpoint&&
                    LanguageTag=Left(Language(),2),
                    CheckpointText),
                
                Description,
                    LookUp(colCheckpointsText,
                    Title=aSource.Title&&
                    Checkpoint=aSource.Checkpoint&&
                    LanguageTag=Left(Language(),2),
                    Description)
                    
            ),
            "Pos",If(CheckpointsSortDescending,
            SortOrder.Descending,SortOrder.Ascending)
        )
    );
    If(CountRows(MyCheckpoints)>0,
        If(IsBlank(SelectedCheckpoint),
            Set(SelectedCheckpoint,
            First(MyCheckpoints).Checkpoint)
        );
        Set(CheckpointSelected,true);
    ,//Else
        Set(SelectedCheckpoint,"");
        Set(CheckpointSelected,true);
    );
    Set(FooterText,"");
    Set(_ShowLoadingHint,false); 
);
//-----------------------------------------------------
If(CheckpointSelected,

    Set(SelectedCheckpointRecord,
        First(Filter(MyCheckpoints,
        Title=SelectedChecklist&&
        Checkpoint=SelectedCheckpoint))
    );

    Set(SCheckpointPos,
        SelectedCheckpointRecord.Pos);
    Set(SCheckpointText,
        SelectedCheckpointRecord.CheckpointText);
    
    Set(SCheckpointDescription,
        SelectedCheckpointRecord.Description);
    Set(SCheckpointDescription,
        Substitute(SCheckpointDescription,Char(10),"<br>")
    );

    Set(SCheckpointRequired,
        SelectedCheckpointRecord.Required);

    Set(SCheckpointStatus1,
        SelectedCheckpointRecord.Status1);
    Set(SCheckpointStatus2,
        SelectedCheckpointRecord.Status2);
    Set(SCheckpointStatus3,
        SelectedCheckpointRecord.Status3);
    Set(SCheckpointStatus4,
        SelectedCheckpointRecord.Status4);
    Set(SCheckpointStatus5,
        SelectedCheckpointRecord.Status5);

    Set(SCheckpointFlexForm,
        SelectedCheckpointRecord.FlexForm);

    //FlexForm auseinandernehmen 
    Clear(colFlexForm);
    //result of the Split() function is a Value
    ForAll(Split(SCheckpointFlexForm,"DisplayOrder:"),
        If(!IsBlank(Value) &&
            Value(First(Split(Value,",")).Value)<6,
            Collect(colFlexForm,
                {
                    Pos:SCheckpointPos,
                    DisplayOrder:
                        First(Split(Value,",")).Value,
                    Definition:
                        "DisplayOrder:"&Value,
                    Question:
                        First(Split(
                        Last(Split(Value,"Question:")).Value
                        ,",")).Value,
                    QuestionText:
                        LookUp(
                        MyFlexFields,
                        Title=First(Split(
                        Last(Split(Value,"Question:")).Value
                        ,",")).Value,
                        FlexFieldText),
                    ResponseType:
                        First(Split(
                        Last(Split(Value,"ResponseType:")).Value
                        ,",")).Value,
                    Choices:
                        If("Choices" in Value,
                            First(Split(
                            Last(Split(Value,"Choices:")).Value
                            ,",")).Value
                        )
                }
            )
        )
    );

    Set(SCheckpointF1,
        LookUp(colFlexForm,Value(DisplayOrder)=1,Definition));
    Set(SCheckpointF1Label,
        LookUp(colFlexForm,Value(DisplayOrder)=1,QuestionText));

    Set(SCheckpointF2,
        LookUp(colFlexForm,Value(DisplayOrder)=2,Definition));
    Set(SCheckpointF2Label,
        LookUp(colFlexForm,Value(DisplayOrder)=2,QuestionText));

    Set(SCheckpointF3,
        LookUp(colFlexForm,Value(DisplayOrder)=3,Definition));
    Set(SCheckpointF3Label,
        LookUp(colFlexForm,Value(DisplayOrder)=3,QuestionText));

    Set(SCheckpointF4,
        LookUp(colFlexForm,Value(DisplayOrder)=4,Definition));
    Set(SCheckpointF4Label,
        LookUp(colFlexForm,Value(DisplayOrder)=4,QuestionText));

    Set(SCheckpointF5,
        LookUp(colFlexForm,Value(DisplayOrder)=5,Definition));
    Set(SCheckpointF5Label,
        LookUp(colFlexForm,Value(DisplayOrder)=5,QuestionText));


    Set(CheckpointSelected,false);

);
//-----------------------------------------------------
