Rules.InvariantScript:
Set (ShowDeleteDialog,false);
Set(_ShowLoadingHint,true);
//--------------------------------------------------
UpdateContext({locInputCheck:true});
//--------------------------------------------------
// Title
If(IsBlank(DataCardValue8_3.Text), 
    UpdateContext({locInputCheck:false});
    Notify("Title must not be empty");
);
// Pos
If(IsBlank(DataCardValue5.Text), 
    UpdateContext({locInputCheck:false});
    Notify("Checkpoint position must not be empty");
);
// Checkpoint
If(IsBlank(DataCardValue4.Text), 
    UpdateContext({locInputCheck:false});
    Notify("Checkpoint must not be empty");
);
//CheckpointText
If(IsBlank(DataCardValue11_2.Text),
    UpdateContext({locInputCheck:false});
    Notify("Checkpoint name must not be empty");
);
//Status1
If(IsBlank(DCPossibleStatus1.Selected.Status),
    UpdateContext({locInputCheck:false});
    Notify("Status1 must not be empty");
);
//Status2
If(!IsBlank(DCPossibleStatus2.Selected.Status),
    If(SCheckpointStatus2 = SCheckpointStatus1 ||
       SCheckpointStatus2 = SCheckpointStatus3 ||
       SCheckpointStatus2 = SCheckpointStatus4 ||
       SCheckpointStatus2 = SCheckpointStatus5,
        UpdateContext({locInputCheck:false});
        Notify("Status2 is not unique");
    )
);
//Status3
If(!IsBlank(DCPossibleStatus3.Selected.Status),
    If(SCheckpointStatus3 = SCheckpointStatus1 ||
       SCheckpointStatus3 = SCheckpointStatus2 ||
       SCheckpointStatus3 = SCheckpointStatus4 ||
       SCheckpointStatus3 = SCheckpointStatus5,
        UpdateContext({locInputCheck:false});
        Notify("Status3 is not unique");
    )
);
//Status4
If(!IsBlank(DCPossibleStatus4.Selected.Status),
    If(SCheckpointStatus4 = SCheckpointStatus1 ||
       SCheckpointStatus4 = SCheckpointStatus2 ||
       SCheckpointStatus4 = SCheckpointStatus3 ||
       SCheckpointStatus4 = SCheckpointStatus5,
        UpdateContext({locInputCheck:false});
        Notify("Status4 is not unique");
    )
);
//Status5
If(!IsBlank(DCPossibleStatus5.Selected.Status),
    If(SCheckpointStatus5 = SCheckpointStatus1 ||
       SCheckpointStatus5 = SCheckpointStatus2 ||
       SCheckpointStatus5 = SCheckpointStatus3 ||
       SCheckpointStatus5 = SCheckpointStatus4,
        UpdateContext({locInputCheck:false});
        Notify("Status5 is not unique");
    )
);
//--------------------------------------------------
//Daten speichern
//If(locInputCheck, 
//    SubmitForm(EditForm1_3); 
//);
//--------------------------------------------------
If(locInputCheck, 
    Patch(MyCheckpoints, 
        LookUp(MyCheckpoints,
        Title=SelectedChecklist&&
        Checkpoint=SelectedCheckpoint),
        {
            CheckpointText:DataCardValue11_2.Text,
            Description:DataCardValue10_2.Text,
            Status1:DCPossibleStatus1.Selected.Status,
            Status2:DCPossibleStatus2.Selected.Status,
            Status3:DCPossibleStatus3.Selected.Status,
            Status4:DCPossibleStatus4.Selected.Status,
            Status5:DCPossibleStatus5.Selected.Status,
            FlexForm:SCheckpointFlexForm,
            Required:DataCardValue6.Value
        }
    );    

    If(!IsEmpty(Errors([@MyChecklists])),
        Set(locErrorCode,
            "Error! Could not add the new record."
        );
        Notify(
            locErrorCode,
            //LookUp(MyUVVSettings;
            //Title="Fehler beim Speichern!").Wert;
            NotificationType.Error, 
            2000
        );
    , //Else
        Set(locErrorCode,"");
    );

    If(IsBlank(locErrorCode) && OnlineStatus=1,

        
        //Record available?
        Set(PatchID,0);
        Set(PatchID,
            LookUp(pssChecklistsCheckpoints,
                Title=SelectedChecklist&&
                Checkpoint=SelectedCheckpoint
            ).ID
        );
        Patch(pssChecklistsCheckpoints, 
            If(PatchID>0,
                LookUp(pssChecklistsCheckpoints,ID=PatchID)
            ,//Else
                Defaults(pssChecklistsCheckpoints)
            ),
            {
                Title:DataCardValue8_3.Text,
                Pos:Value(DataCardValue5.Text),
                Checkpoint:DataCardValue4.Text,
                Status1:DCPossibleStatus1.Selected.Status,
                Status2:DCPossibleStatus2.Selected.Status,
                Status3:DCPossibleStatus3.Selected.Status,
                Status4:DCPossibleStatus4.Selected.Status,
                Status5:DCPossibleStatus5.Selected.Status,
                FlexForm:SCheckpointFlexForm,
                Required:DataCardValue6.Value
            }
        );    


        //Record available?
        Set(PatchID,0);
        Set(PatchID,
            LookUp(pssChecklistsCheckpointsText,
                Title=SelectedChecklist&&
                Checkpoint=SelectedCheckpoint&&
                LanguageTag=Left(Language(),2)
            ).ID
        );
        Patch(pssChecklistsCheckpointsText, 
            If(PatchID>0,
                LookUp(pssChecklistsCheckpointsText,ID=PatchID)
            ,//Else
                Defaults(pssChecklistsCheckpointsText)
            ),
            {
                Title:DataCardValue8_3.Text,
                Checkpoint:DataCardValue4.Text,
                CheckpointText:DataCardValue11_2.Text,
                Description:DataCardValue10_2.Text,
                LanguageTag:Left(Language(),2)
            }
        );    
    );
);
//--------------------------------------------------
//Daten neu einlesen
Set(_ShowLoadingHint,false);
If(locInputCheck, 
    Set(InitialReadCheckpoints,true);
    Set(ReadCheckpoints,true);
    Set(CheckpointSelected,true);
    Navigate(ScreenCheckpoints);
);

//ndern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)

//ndern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)

//ndern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)

If(IsBlank(DataCardValue11_2.Text),
    "Checkpoint name must not be empty"
)

//ndern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)

Set(SCheckpointStatus1,DCPossibleStatus1.Selected.Status);

If(IsBlank(SCheckpointStatus1),
    Set(SCheckpointStatus2,Blank());
    Set(SCheckpointStatus3,Blank());
    Set(SCheckpointStatus4,Blank());
    Set(SCheckpointStatus5,Blank());
);

If(SCheckpointStatus1="without identified defects" ||
   SCheckpointStatus1="OK",
    Set(SCheckpointStatus2,"minor defects");
    Set(SCheckpointStatus3,"significant defects");
    Set(SCheckpointStatus4,"dangerous defects");
    Set(SCheckpointStatus5,Blank());
);

If(SCheckpointStatus1="Yes",
    Set(SCheckpointStatus2,"No");
    //Set(SCheckpointStatus3;Blank());;
    //Set(SCheckpointStatus4;Blank());;
    //Set(SCheckpointStatus5;Blank());;
);

//ndern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)
