Rules.InvariantScript:
If(IsBlank(SelectedFlexField) &&
    CountRows(MyFlexFields)>0,
    Set(SelectedFlexField,First(MyFlexFields).Title);
    Set(FlexFieldSelected,true);
);
//-----------------------------------------------------
If(Homepage1="ScreenFlexFields",Set(Homepage1,""));
If(Homepage2="ScreenFlexFields",Set(Homepage2,""));
If(Homepage3="ScreenFlexFields",Set(Homepage3,""));
If(Homepage4="ScreenFlexFields",Set(Homepage4,""));
If(IsBlank(Homepage1),
    Set(Homepage1,"ScreenFlexFields"),
If(IsBlank(Homepage2),
    Set(Homepage2,"ScreenFlexFields"),
If(IsBlank(Homepage3),    
    Set(Homepage3,"ScreenFlexFields"),
    Set(Homepage4,"ScreenFlexFields")
)));
//-----------------------------------------------------

Set(ShowDeleteDialog,false);
//--------------------------------------------------
If(Homepage1="ScreenChecklists",Set(Homepage1,""));
If(Homepage2="ScreenChecklists",Set(Homepage2,""));
If(Homepage3="ScreenChecklists",Set(Homepage3,""));
If(Homepage4="ScreenChecklists",Set(Homepage4,""));

If(!IsBlank(Homepage4),
    //Notify("Homepage4...");;
    Switch(Homepage4, 
        "ScreenChecklists", 
            Set(Homepage4,"");
            Navigate(ScreenFlexFields),
        //Standardergebnis
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage3),
    //Notify("Homepage3...");;
    Switch(Homepage3, 
        "ScreenChecklists", 
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenFlexFields),
        //Standardergebnis
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage2),
    //Notify("Homepage2...");;
    Switch(Homepage2, 
        "ScreenChecklists", 
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenFlexFields),
        //Standarsergebnis
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
    //Notify("Homepage1...");;
    Switch(Homepage1, 
        "ScreenChecklists", 
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenFlexFields),
        //Standarsergebnis
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
)));

Set(ShowDeleteDialog,false);
Set (locErrorCode, "");
//--------------------------------------------------------
// Delete flex field
If(IsBlank(locErrorCode),

    // remove from collection
    RemoveIf(MyFlexFields,Title=SelectedFlexField); 
    
    //all good?
    If(!IsEmpty(Errors([@MyFlexFields])),
        Set(locErrorCode, 
            LookUp(colLanguage,Title="Error! Was not able to delete the field",Wert)
        );
        Notify(
            locErrorCode,
            NotificationType.Error,2000
        );
    ,// Else
        Set (locErrorCode, "");
    );

    // Save checklists locally
    If(MyDeviceType="Mobile" && IsBlank(locErrorCode),
        SaveData(MyFlexFields,"MyFlexFields")  
    );

    //Remove from SP list
    If(IsBlank(locErrorCode) && OnlineStatus=1,

        RemoveIf(pssChecklistsFlexFields,
            Title=SelectedFlexField); 
        
        RemoveIf(pssChecklistsFlexFieldsText,
            Title=SelectedFlexField); 

    );

    If(CountRows(MyFlexFields)>0,
        Set(SelectedFlexField,
            First(MyFlexFields).Title
        );
        Set(FlexFieldSelected,true);
    ,//Else
        Set(SelectedFlexField,"");
        Set(FlexFieldSelected,false);
    );
    Set(InitialReadFlexFields,true);
    Set(ReadFlexFields,true);

);

If(Connection.Connected,
    Set (OnlineStatus, 1)
,// Else    
    Set (OnlineStatus, 0)
);
//------------------------------------------------
//Load FlexFields from SP
If(IsBlank(InitialReadFlexFields)||InitialReadFlexFields,
    Set(_ShowLoadingHint,true);
    //-----------------------------------------------------
    Set(InitialReadFlexFields,false);
    //-----------------------------------------------------
    If(IsBlank(FlexFieldsSortDescending),
        Set(FlexFieldsSortDescending,false)
    );
    //-----------------------------------------------------
    If(OnlineStatus=1,
        //-------------------------------------------------
        //load flex fields from SP list
        Set(FooterText,"Loading flex fields");
        Refresh(pssChecklistsFlexFields);
        ClearCollect(colFlexFields,
            ShowColumns(
                Filter(pssChecklistsFlexFields,
                    Load=true
                ),
                ID,Title,ResponseType,Choices,Load
            )
        );
        // Save flex fields locally
        If(MyDeviceType="Mobile",
            SaveData(colFlexFields,"FlexFields")  
        );
        //-------------------------------------------------
        //load flex fields text from SP list
        Set(FooterText,"Loading flex fields text");
        Refresh(pssChecklistsFlexFieldsText);
        ClearCollect(colFlexFieldsText,
            ShowColumns(
                Filter(pssChecklistsFlexFieldsText,
                    Load=true
                ),
                ID,Title,Text,LanguageTag
            )
        );
        // Save checklists text locally
        If(MyDeviceType="Mobile",
            SaveData(colFlexFieldsText,"FlexFieldsText")  
        );
    ,//Else
        If(MyDeviceType="Mobile",
            Clear(colFlexFields);
            Clear(colFlexFieldsText);
            
            Set(FooterText,"Loading flex fields from cache");
            LoadData(colFlexFields,"FlexFields",true);

            Set(FooterText,"Loading flex fields text from cache");
            LoadData(colFlexFieldsText,"FlexFieldsText",true);

        )
    );
    Set(FooterText,"");
    Set(_ShowLoadingHint,false); 
);
//---------------------------------------------
//Load flex fields from local collection
If(IsBlank(ReadFlexFields)||ReadFlexFields,
    Set(_ShowLoadingHint,true);
    Set(ReadFlexFields,false);
    Set(FooterText,"Loading MyFlexFields");
    ClearCollect(MyFlexFields,
        SortByColumns(
            AddColumns(
                ShowColumns(
                    colFlexFields,
                    Title,ResponseType,Choices
                ) As aSource,

                FlexFieldText,
                    LookUp(colFlexFieldsText,
                    Title=aSource.Title&&
                    LanguageTag=Left(Language(),2),
                    Text)

            ),
            "FlexFieldText",If(FlexFieldsSortDescending,
            SortOrder.Descending,SortOrder.Ascending)
        )
    );
    Collect(MyFlexFields, 
    {
        Title:"",
        ResponseType:"",
        Choices:"",
        FlexFieldText:""
    });
    //-----------------------------------------------------
    If(CountRows(MyFlexFields)>0,
        If(IsBlank(SelectedFlexField),
            Set(SelectedFlexField,First(MyFlexFields).Title)
        );
        Set(FlexFieldSelected,true);
    ,//Else
        Set(SelectedFlexField,"");
        Set(FlexFieldSelected,false);
    );
    Set(FooterText,"");
    Set(_ShowLoadingHint,false); 
);
//------------------------------------------------
If(FlexFieldSelected,
    Set(SFlexFieldText,
        LookUp(MyFlexFields,Title=SelectedFlexField,FlexFieldText)
    );
    Set(SFlexFieldResponseType,
        LookUp(MyFlexFields,Title=SelectedFlexField,ResponseType)
    );
    Set(SFlexFieldChoices,
        LookUp(MyFlexFields,Title=SelectedFlexField,Choices)
    );
    Set(FlexFieldSelected,false);
);
//-----------------------------------------------------
Set(_ShowLoadingHint,false);
Set(FooterText,"");
