Rules.InvariantScript:
Set (ShowDeleteDialog,false);
Set(_ShowLoadingHint,true);
//--------------------------------------------------
UpdateContext({locInputCheck:true});
//--------------------------------------------------
// Title
If(IsBlank(DataCardValue3.Text), 
    UpdateContext({locInputCheck:false});
    Notify("Title must not be empty");
);
If(!IsBlank(DataCardValue3.Text), 
    If(!IsBlank(LookUp(MyFlexFields,Title=DataCardValue3.Text,Title)),
        UpdateContext({locInputCheck:false});
        Notify("Title must not exists");
    );
);
//--------------------------------------------------
//Daten speichern
//If(locInputCheck, 
//    SubmitForm(EditForm1_3); 
//);
//--------------------------------------------------
If(locInputCheck, 

    //0=Edit;1=Add
    Patch(MyFlexFields, 
        If(EditForm1_26.Mode=0,
            LookUp(MyFlexFields,Title=SelectedFlexField)
        ,//Else
            Defaults(MyFlexFields)
        ),
        {
            Title:DataCardValue3.Text
        }
    );    

    If(!IsEmpty(Errors([@MyFlexFields])),
        Set(locErrorCode,
            "Error! Could not save the new entry!"
        );
        Notify(
            locErrorCode,
            //LookUp(MyUVVSettings;
            //Title="Fehler beim Speichern!").Wert;
            NotificationType.Error, 
            2000
        );
    , //Else
        Set(locErrorCode,"");
        Set(SelectedFlexField,DataCardValue3.Text);
        Set(FlexFieldSelected,true);
    );

    If(IsBlank(locErrorCode) && OnlineStatus=1,
        //0=Edit;1=Add
        Patch(pssChecklistsFlexFields, 
            If(EditForm1_26.Mode=0,
                LookUp(pssChecklistsFlexFields,
                Title=SelectedFlexField)
            ,//Else
                Defaults(pssChecklistsFlexFields)
            ),
            {
                Title:DataCardValue3.Text
            }
        );    
    );
);
//--------------------------------------------------
//Daten neu einlesen
Set(_ShowLoadingHint,false);
If(locInputCheck, 
    Set(FlexFieldSelected,true);
    Set(InitialReadFlexFields,true);
    Set(ReadFlexFields,true);    
    EditForm(EditForm1_27);
    Navigate(ScreenFlexFieldTextEdit);
);

//Ändern nur, wenn noch kein Wert vorhanden
If(Self.DisplayMode=DisplayMode.Edit,
    RGBA(255, 255, 0, 1) //gelb
,
    RGBA(255, 255, 255, 1)
)
