If(locInputCheck, 

    //0=Edit;1=Add
    Patch(MyActivities, 
        Defaults(MyActivities),
        {
            ID:Max(MyActivities,ID)+1,
            KEY:
                User().FullName 
                &"-"
                &Text(Now(),"yyyymmddhhmmss","de"),
            Title:DCChecklist1_2.Selected.Title,
            User:User().FullName,
            UserEMail:User().Email,
            Date:
                If(Not IsBlank(DateValue2.SelectedDate), 
                    DateTime(
                        Year(DateValue2.SelectedDate), 
                        Month(DateValue2.SelectedDate), 
                        Day(DateValue2.SelectedDate), 
                        Value(HourValue2.Selected.Value), 
                        Value(MinuteValue2.Selected.Value),
                        0
                    )
                ),
            DocumentStatus:"New",
            Comments:""
        }
    );    

    If(!IsEmpty(Errors([@MyActivities])),
        Set(locErrorCode,
            "Error! Could not save the new entry!"
        );
        Notify(
            locErrorCode,
            //LookUp(MyUVVSettings;
            //Title="Fehler beim Speichern!").Wert;
            NotificationType.Error, 
            2000
        );
    , //Else
        Set(locErrorCode,"");
        Set(SelectedActivity,Max(MyActivities,ID));
        Set(ActivitySelected,true);
    );

    If( locInputCheck && 
        IsBlank(locErrorCode) && 
        MyDeviceType="Mobile",
        SaveData(MyActivities,"Activities")
    );

);
