ForAll(SComponents As aSource, 
    If(IsBlank(aSource.Status)&&aSource.Required=true,
        If(LookUp(MyActivitiesP,
            KEY=SActivityKEY && 
            Pos=aSource.Pos,
            KEY)=Blank(),
            Collect(MyActivitiesP,
            {
                KEY:SActivityKEY,
                ParentID:SelectedActivity,
                Pos:aSource.Pos,
                Checkpoint:aSource.Checkpoint,
                CheckpointText:aSource.CheckpointText,

                Status:
                    Last(Split(
                    First(Split(aSource.PossibleStatus,";")).Value,
                    "Status.")).Value,
            
                StatusText:
                    LookUp(MyStatus,
                    Status=
                    Last(Split(
                    First(Split(aSource.PossibleStatus,";")).Value,
                    "Status.")).Value,
                    StatusText),

                DefectClass:
                    LookUp(MyStatus,
                    Status=
                    Last(Split(
                    First(Split(aSource.PossibleStatus,";")).Value,
                    "Status.")).Value,
                    DefectClass),

                Comments:aSource.Comments,
                F1:aSource.F1,
                F2:aSource.F2,
                F3:aSource.F3,
                F4:aSource.F4,
                F5:aSource.F5,
                F1Label:aSource.F1Label,
                F2Label:aSource.F2Label,
                F3Label:aSource.F3Label,
                F4Label:aSource.F4Label,
                F5Label:aSource.F5Label
            })
        ,//Else
            Patch(MyActivitiesP,
                LookUp(MyActivitiesP,
                    KEY=SActivityKEY && Pos=aSource.Pos
                ),
                {
                KEY:SActivityKEY,
                ParentID:SelectedActivity,
                Pos:aSource.Pos,
                Checkpoint:aSource.Checkpoint,
                CheckpointText:aSource.CheckpointText,
                Status:aSource.Status,
                StatusText:aSource.StatusText,
                DefectClass:aSource.DefectClass,
                Comments:aSource.Comments,
                F1:aSource.F1,
                F2:aSource.F2,
                F3:aSource.F3,
                F4:aSource.F4,
                F5:aSource.F5,
                F1Label:aSource.F1Label,
                F2Label:aSource.F2Label,
                F3Label:aSource.F3Label,
                F4Label:aSource.F4Label,
                F5Label:aSource.F5Label
                }
            )
        )
    )
);
//--------------------------------------------------
//Save activity checkpoints data in cache, on mobile devices
If(MyDeviceType="Mobile",
    SaveData(MyActivitiesP,"ActivitiesP")  
);
//--------------------------------------------------
Set(ActivitySelected,true);

Set(ShowDeleteDialog,false);
Set(_ShowLoadingHint,true);

Set(SACheckpointPos,ThisItem.Pos);
Set(SACheckpoint,ThisItem.Checkpoint);
Set(SACheckpointText,ThisItem.CheckpointText);
Set(SACheckpointStatus,ThisItem.Status);
Set(SACheckpointStatusText,ThisItem.StatusText);
Set(SACheckpointDefectClass,ThisItem.DefectClass);
Set(SACheckpointComments,ThisItem.Comments);

Clear(colFlexForm);
If(!IsBlank(ThisItem.FlexForm),
    
    ForAll(Split(ThisItem.FlexForm,"DisplayOrder:"),
        If(!IsBlank(Value),
            Collect(colFlexForm,
                {
                    DisplayOrder:
                        First(Split(Value,",")).Value,
                    Question:
                        First(Split(
                        Last(Split(Value,"Question:")).Value
                        ,",")).Value,
                    QuestionText:
                        LookUp(
                        MyFlexFields,
                        Title=First(Split(
                        Last(Split(Value,"Question:")).Value
                        ,",")).Value,
                        FlexFieldText),
                    ResponseType:
                        First(Split(
                        Last(Split(Value,"ResponseType:")).Value
                        ,",")).Value,
                    Choices:
                        /*
                        If("Choices" in Value;
                        First(Split(
                        Last(Split(Value;"Choices:")).Value
                        ;",")).Value
                        );
                        */
                        First(Split(
                        Last(Split(Value,"Choices:")).Value
                        ,",")).Value,

                    Calculation:
                        If("Calculation" in Value,
                        First(Split(
                        Last(Split(Value,"Calculation:")).Value
                        ,",")).Value
                    ),
                    Response:""
                    
                }
            )
        )
    );

    //Die Fragen aus der FlexForm in die 
    //Collection SComponents bertragen
    ForAll(colFlexForm As aPatch,
        Patch(SComponents,
            LookUp(SComponents,Pos=SACheckpointPos),
            Switch(aPatch.DisplayOrder,
                
                "1",
                    {F1Title:aPatch.Question};
                    {F1Label:aPatch.QuestionText};
                    {F1:
                        If(IsBlank(ThisItem.F1) &&
                            (aPatch.ResponseType="Combobox" ||
                            aPatch.ResponseType="Radio"),
                            First(Split(aPatch.Choices,";")).Value
                        ,
                            ThisItem.F1
                        )
                    },
                
                "2",
                    {F2Title:aPatch.Question};
                    {F2Label:aPatch.QuestionText};
                    {F2:
                        If(IsBlank(ThisItem.F2) &&
                            (aPatch.ResponseType="Combobox" ||
                            aPatch.ResponseType="Radio"),
                            First(Split(aPatch.Choices,";")).Value
                        ,
                            ThisItem.F2
                        )
                    },
                
                "3",
                    {F3Title:aPatch.Question};
                    {F3Label:aPatch.QuestionText};
                    {F3:
                        If(IsBlank(ThisItem.F3) &&
                            (aPatch.ResponseType="Combobox" ||
                            aPatch.ResponseType="Radio"),
                            First(Split(aPatch.Choices,";")).Value
                        ,
                            ThisItem.F3
                        )
                    },
                    
                "4",
                    {F4Title:aPatch.Question};
                    {F4Label:aPatch.QuestionText};
                    {F4:
                        If(IsBlank(ThisItem.F4) &&
                            (aPatch.ResponseType="Combobox" ||
                            aPatch.ResponseType="Radio"),
                            First(Split(aPatch.Choices,";")).Value
                        ,
                            ThisItem.F4
                        )
                    },
                    
                "5",
                    {F5Title:aPatch.Question};
                    {F5Label:aPatch.QuestionText};
                    {F5:
                        If(IsBlank(ThisItem.F5) &&
                            (aPatch.ResponseType="Combobox" ||
                            aPatch.ResponseType="Radio"),
                            First(Split(aPatch.Choices,";")).Value
                        ,
                            ThisItem.F5
                        )
                    }
                    
            )
        )
    );
);
    
