Rules.InvariantScript:
If(IsBlank(SelectedStatus) &&  CountRows(MyStatus)>0,
    Set(SelectedStatus,
        First(Filter(MyStatus,Status<>Blank())).Status
    );
    Set(StatusSelected,true);
);
//-----------------------------------------------------
If(Homepage1="ScreenStatus",Set(Homepage1,""));
If(Homepage2="ScreenStatus",Set(Homepage2,""));
If(Homepage3="ScreenStatus",Set(Homepage3,""));
If(Homepage4="ScreenStatus",Set(Homepage4,""));
If(IsBlank(Homepage1),
    Set(Homepage1,"ScreenStatus"),
If(IsBlank(Homepage2),
    Set(Homepage2,"ScreenStatus"),
If(IsBlank(Homepage3),    
    Set(Homepage3,"ScreenStatus"),
    Set(Homepage4,"ScreenStatus")
)));
//-----------------------------------------------------

Set(ShowDeleteDialog,false);
//--------------------------------------------------
If(Homepage1="ScreenChecklists",Set(Homepage1,""));
If(Homepage2="ScreenChecklists",Set(Homepage2,""));
If(Homepage3="ScreenChecklists",Set(Homepage3,""));
If(Homepage4="ScreenChecklists",Set(Homepage4,""));

If(!IsBlank(Homepage4),
    //Notify("Homepage4...");;
    Switch(Homepage4, 
        "ScreenChecklists", 
            Set(Homepage4,"");
            Navigate(ScreenStatus),
        //Standardergebnis
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage3),
    //Notify("Homepage3...");;
    Switch(Homepage3, 
        "ScreenChecklists", 
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenStatus),
        //Standardergebnis
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
If(!IsBlank(Homepage2),
    //Notify("Homepage2...");;
    Switch(Homepage2, 
        "ScreenChecklists", 
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenStatus),
        //Standarsergebnis
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
,//Else
    //Notify("Homepage1...");;
    Switch(Homepage1, 
        "ScreenChecklists", 
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(ScreenStatus),
        //Standarsergebnis
            Set(Homepage1,"");
            Set(Homepage2,"");
            Set(Homepage3,"");
            Set(Homepage4,"");
            Navigate(StartScreen)
    )
)));

SortByColumns(
    Filter(MyStatus, 
        Status<>""&&
        (StatusSearchBox_2.Text in Status||
        StatusSearchBox_2.Text in StatusText)
    ),
    //Sort
    //"SortNo"; 
    "StatusText", 
        If(StatusSortDescending,
        SortOrder.Descending,SortOrder.Ascending)
)

If(Connection.Connected,
    Set (OnlineStatus, 1)
,// Else    
    Set (OnlineStatus, 0)
);
//------------------------------------------------
//Load Status
If(IsBlank(InitialReadStatus)||InitialReadStatus=true,
    Set(_ShowLoadingHint,true);
    //-----------------------------------------------------
    Set(InitialReadStatus,false);
    //-----------------------------------------------------
    If(IsBlank(StatusSortDescending),
        Set(StatusSortDescending,false)
    );
    //-----------------------------------------------------
    If(OnlineStatus=1,
        //-------------------------------------------------
        //load Status from SP list
        Set(FooterText,"Loading Status");
        Refresh(pssChecklistsStatus);
        ClearCollect(colStatus,
            AddColumns(
                ShowColumns(
                    //Filter(pssChecklistsStatus;SortNo>=0);
                    pssChecklistsStatus,
                    ID,Status,DefectClass,Icon
                ) As aSource,
                StatusText,                        
                    LookUp(pssChecklistsStatusText,
                    Status=aSource.Status &&
                    LanguageTag=Left(Language(),2),
                    StatusText)
            )
        );
        // Save status locally
        If(MyDeviceType="Mobile",
            SaveData(colStatus,"Status")  
        );
    ,//Else
        If(MyDeviceType="Mobile",
            Clear(colStatus);
            Set(FooterText,"Loading Status from cache");
            LoadData(colStatus,"Status",true);
        )
    );
    ClearCollect(MyStatus,
        SortByColumns(
            ShowColumns(
                colStatus,
                Status,DefectClass,Icon,StatusText
            ),
            "StatusText",SortOrder.Ascending
        )
    );

    If(CountRows(MyStatus)>0,
        If(IsBlank(SelectedStatus),
            Set(SelectedStatus,
                First(Filter(MyStatus,Status<>Blank())).Status
            )
        );
        Set(StatusSelected,true);
    ,//Else
        Set(SelectedStatus,"");
        Set(StatusSelected,false);
    );

    Set(FooterText,"");
    Set(_ShowLoadingHint,false); 

);
//------------------------------------------------
If(StatusSelected=true,
    Set(SStatusDefectClass,
        LookUp(MyStatus,Status=SelectedStatus,DefectClass)
    );
    Set(SStatusIcon,
        LookUp(MyStatus,Status=SelectedStatus,Icon)
    );
    Set(SStatusText,
        LookUp(MyStatus,Status=SelectedStatus,StatusText)
    );
    Set(StatusSelected,false);
);
//-----------------------------------------------------
Set(_ShowLoadingHint,false);
Set(FooterText,"");

Set(ShowDeleteDialog,false);
Set (locErrorCode, "");
//--------------------------------------------------------
// Delete Status
If(IsBlank(locErrorCode),

    // remove from collection
    RemoveIf(MyStatus,Status=SelectedStatus); 
    
    //all good?
    If(!IsEmpty(Errors([@MyStatus])),
        Set(locErrorCode, 
            LookUp(colLanguage,Title="Error! Was not able to delete the status",Wert)
        );
        Notify(
            locErrorCode,
            NotificationType.Error,2000
        );
    ,// Else
        Set (locErrorCode, "");
    );

    // Save Status locally
    If(IsBlank(locErrorCode),
        RemoveIf(colStatus,Status=SelectedStatus); 
        If(MyDeviceType="Mobile" && IsBlank(locErrorCode),
            SaveData(colStatus,"Status")  
        );
    );

    //Remove from SP list
    If(IsBlank(locErrorCode) && OnlineStatus=1,

        RemoveIf(pssChecklistsStatus,
            Status=SelectedStatus); 

        RemoveIf(pssChecklistsStatusText,   Status=SelectedStatus); 
    );

    If(CountRows(MyStatus)>0,
        Set(SelectedStatus,
            First(Filter(MyStatus,Status<>Blank())).Status
        );
        Set(StatusSelected,true);
    ,//Else
        Set(SelectedStatus,"");
        Set(StatusSelected,false);
    );
    Set(InitialReadStatus,true);
    Set(ReadStatus,true);

);
